query.sparql:
SELECT DISTINCT ?uri ?string 
WHERE {
        ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/class/yago/StatesOfGermany> .
        { ?uri <http://dbpedia.org/property/rulingParty> 'SPD'@en . } UNION { ?uri <http://dbpedia.org/property/rulingParty> <http://dbpedia.org/resource/Social_Democratic_Party_of_Germany> . } 
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string
WHERE {
        ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/class/yago/StatesOfTheUnitedStates> .
        ?uri <http://dbpedia.org/property/mineral> ?mineral .
        FILTER (regex(?mineral,'Gold')) .
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE {       
        ?cave <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Cave> . 
        ?cave <http://dbpedia.org/ontology/location> ?uri .
        ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Country> .
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. FILTER (lang(?string) = 'en') }
} 
GROUP BY ?uri ?string
HAVING (COUNT(?cave) > 2)

SELECT DISTINCT ?uri ?string 
WHERE {
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/City> .
        ?uri <http://dbpedia.org/ontology/isPartOf> <http://dbpedia.org/resource/New_Jersey> .
        ?uri <http://dbpedia.org/property/populationTotal> ?inhabitants .
        FILTER (?inhabitants > 100000) .
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE {       
        <http://dbpedia.org/resource/Annapurna> <http://dbpedia.org/ontology/elevation> ?elevation .
        ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Mountain> . 
        ?uri <http://dbpedia.org/ontology/elevation> ?otherelevation .
        FILTER (?otherelevation < ?elevation) .
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. FILTER (lang(?string) = 'en') } 
} 
ORDER BY DESC(?otherelevation) LIMIT 1

SELECT DISTINCT ?uri ?string 
WHERE {       
        ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Bridge> .
        ?uri <http://dbpedia.org/property/design> ?design .
        <http://dbpedia.org/resource/Manhattan_Bridge> <http://dbpedia.org/property/design> ?mdesign . 
        FILTER (regex(?design, ?mdesign)).
        FILTER (?uri != <http://dbpedia.org/resource/Manhattan_Bridge>) . 
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE {
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/class/yago/StatesOfTheUnitedStates> .
        ?uri <http://dbpedia.org/property/densityrank> ?rank .
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. FILTER (lang(?string) = 'en') }
} 
ORDER BY ASC(?rank) LIMIT 1

SELECT DISTINCT ?uri ?string 
WHERE {
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/class/yago/EuropeanUnionMemberStates> .
        { ?uri <http://dbpedia.org/property/currency> <http://dbpedia.org/resource/Euro> . } 
        UNION 
        { ?uri <http://dbpedia.org/property/currencyCode> 'EUR'@en . }
        UNION 
        { ?uri <http://dbpedia.org/property/currencyCode> 'Euro'@en . }
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string WHERE
{ 
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Country>  .
        ?uri <http://dbpedia.org/ontology/officialLanguage> ?language .
	OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. FILTER (lang(?string) = 'en') }
} 
GROUP BY ?uri ?string
HAVING (COUNT(?language) > 2)

SELECT DISTINCT ?uri ?string 
WHERE {
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Mountain> .
        ?uri <http://dbpedia.org/ontology/locatedInArea> <http://dbpedia.org/resource/Australia> .
        ?uri <http://dbpedia.org/ontology/elevation> ?elevation .
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. FILTER (lang(?string) = 'en') }
} 
ORDER BY DESC(?elevation) LIMIT 1

SELECT DISTINCT ?uri ?string 
WHERE {
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/SoccerClub> .
        { ?uri <http://dbpedia.org/ontology/ground> <http://dbpedia.org/resource/Spain> . }
        UNION 
        { ?uri <http://dbpedia.org/property/ground> ?ground . FILTER (regex(?ground, 'Spain')) }
        OPTIONAL {?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE { 	
	?uri  <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Organisation>  .
	{ ?uri <http://dbpedia.org/property/industry> <http://dbpedia.org/resource/Telecommunications> . } UNION { ?uri <http://dbpedia.org/property/industry> ?industry . FILTER (regex(?industry, 'Telecommunication')) . }
	{ ?uri <http://dbpedia.org/ontology/location> <http://dbpedia.org/resource/Belgium>. } UNION { ?uri <http://dbpedia.org/property/location> <http://dbpedia.org/resource/Belgium>. } UNION { ?uri <http://dbpedia.org/property/locationCountry> 'Belgium'@en . }
	OPTIONAL {?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE { 
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Company>  .
        ?uri <http://dbpedia.org/property/industry> ?industry .
        FILTER regex(?industry,'advertising','i') .
        OPTIONAL {?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE {
	{ ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/President> . }
	UNION
	{ ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/class/yago/Presidents> . }
        ?uri <http://dbpedia.org/ontology/birthDate> ?date .
	FILTER regex(?date, '^1945') .
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE {
        ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Person> .
        { ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/class/yago/PresidentsOfTheUnitedStates>. }
        UNION
        { ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/President>.
          ?uri <http://dbpedia.org/property/title> <http://dbpedia.org/resource/President_of_the_United_States>. }
        ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string. 
        FILTER (lang(?string) = 'en' && !regex(?string,'Presidency','i') && !regex(?string,'and the')) .
}

SELECT DISTINCT ?uri ?string 
WHERE {
	?film <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Film> .
        ?film <http://dbpedia.org/ontology/producer> ?uri .
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
} 
ORDER BY DESC(COUNT(?film)) LIMIT 1

SELECT DISTINCT ?uri ?string 
WHERE {
        <http://dbpedia.org/resource/Nanga_Parbat> <http://dbpedia.org/property/elevationM> ?elevation .
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Mountain> .
        ?uri <http://dbpedia.org/property/elevationM> ?otherelevation .
        FILTER (?otherelevation > ?elevation) .
	OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE {
  ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Actor> .
  { ?uri <http://dbpedia.org/ontology/birthPlace> <http://dbpedia.org/resource/Germany> . }
  UNION 
  { ?uri <http://dbpedia.org/ontology/birthPlace> ?place .
    ?place <http://dbpedia.org/ontology/country> <http://dbpedia.org/resource/Germany> . }
  OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE { 
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Cave> .
        ?uri <http://dbpedia.org/ontology/numberOfEntrances> ?entrance .
        FILTER (?entrance > 3) .
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE {
      ?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/VideoGame> .
      { ?uri <http://dbpedia.org/property/publisher> 'Mean Hamster Software'@en . }
      UNION 
      { ?uri <http://dbpedia.org/ontology/publisher> <http://dbpedia.org/resource/Mean_Hamster_Software> . }
      OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE { 
      { <http://dbpedia.org/resource/Estonia> <http://dbpedia.org/ontology/language> ?uri . } 
      UNION 
      { ?uri <http://dbpedia.org/ontology/spokenIn> <http://dbpedia.org/resource/Estonia> . }
      OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE {
	?uri <http://dbpedia.org/ontology/orderInOffice> '5th President of the United States'@en .
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE {
        ?single <http://dbpedia.org/ontology/album> ?uri .
        ?single <http://xmlns.com/foaf/0.1/name> 'Last Christmas'@en .
        OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string WHERE
{ 
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Country>  .
        ?uri <http://dbpedia.org/property/officialLanguages> ?language .
	OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }		     
} 
ORDER BY DESC(COUNT(?language)) LIMIT 1

SELECT DISTINCT ?uri ?string 
WHERE {
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Organisation> .
        { ?uri <http://dbpedia.org/property/foundation> ?date . } UNION { ?uri <http://dbpedia.org/ontology/formationYear> ?date . }
        FILTER regex(?date,'^1950') .
	OPTIONAL { ?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
}

SELECT DISTINCT ?uri ?string 
WHERE { 
	?uri <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> <http://dbpedia.org/ontology/Mountain>  .
        ?uri <http://dbpedia.org/ontology/elevation> ?elevation .
        OPTIONAL {?uri <http://www.w3.org/2000/01/rdf-schema#label> ?string . FILTER (lang(?string) = 'en') }
} 
ORDER BY DESC(?elevation) LIMIT 1
